    }
    return file
  }
{% endif %}

$(function () {
    //ON PAGE LOAD
    let imgBaseString = "";

    //Get the fields
    const nameElement = $("#msdynce_coursename");
    const instructorElement = $("#instructorname");
    const formatElement = $("#msdynce_coursetype");
    const frequencyElement = $("#frequency");
    const descriptionElement = $("#msdynce_coursedescription");
    const categoryElement = $("#msdynce_coursecategory");
    const capacityElement = $("#msdynce_registerationmaxcapacity");
    const gradeElement = $("#msdynce_coursegradelevel");
    const deadlineElement = $("#deadline");
    const startDateElement = $("#startdate");
    const startTimeElement = $("#starttime");
    const endDateElement = $("#enddate");
    const endTimeElement = $("#endtime");
    const oob_instructor = $("#msdynce_instructorname");
    const oob_deadline = $("#msdynce_registerationdeadline");
    const oob_startdatetime = $("#msdynce_coursestartdateandtime");
    const oob_enddatetime = $("#msdynce_courseenddateandtime");
    const oob_frequency = $("#msdynce_frequency");
    const oob_days = $("#msdynce_coursedays");
    const oob_updateBtn = $("#UpdateButton");
    const oob_description = $("#msdynce_coursedescription");

    oob_updateBtn.remove();

    //get the image
    webapi.safeAjax({
        type: "GET",
        url: "/_api/msdynce_courses({{request.params['id']}})/msdynce_entityimage/?size=full",
        contentType: "application/json",
        success: function (res, status) {
            if (res) {
                imgBaseString = res.value;
                $(".image-preview-area img").attr("src", "data:image/png;base64," + imgBaseString);
            }
        }
    });

    //Fill values with the selected course information
    instructorElement.val(oob_instructor.val());
    if (oob_frequency.val() == "192350000") {
        document.getElementById("frequency").checked = true;
        document.getElementById("enddate").disabled = true;
        isRecurrent = false;
    }
    //Get the description then display the character count   
    $("#characterCount").text((oob_description?.val()?.length ?? 0) + " / " + oob_description?.attr("maxlength"));

    //Save dates as UTC
    let startDate = moment(oob_startdatetime.val());
    let endDate = moment(oob_enddatetime.val());
    let deadline = moment(oob_deadline.val());

    //Then display them in local timezone and with the appropiate format
    document.getElementById("startdate").value = moment(startDate).local().format("YYYY-MM-DD");
    document.getElementById("enddate").value = moment(endDate).local().format("YYYY-MM-DD");
    document.getElementById("starttime").value = moment(startDate).local().format("HH:mm");
    document.getElementById("endtime").value = moment(endDate).local().format("HH:mm");
    document.getElementById("deadline").value = moment(deadline).local().format("YYYY-MM-DD");

    let daysString = oob_days.val().replace(/[\[\]]/g, '');
    daysString = daysString.replace(/{"Value":/g, '');
    daysString = daysString.replace(/}/g, '');
    let daysArray = daysString.split(",");

    $(".day-of-week input[type='checkbox']").each(function () {
        if (daysArray.includes(this.value)) {
            this.checked = true;
            $(this.nextElementSibling).addClass("selected");
            selectedDays.push(this.value);
        }
    });

    //Create Course record
    $('#createButton').on("click", function (e) {
        // Don't perform the default behaviour for the button i.e. navigation
        e.preventDefault();
        //Call validation function for form fields
        let errorMessages = fieldValidations();

        if (errorMessages.length == 0) {
            this.disabled = true;
            $(this).text("{{ settings['ASP/Course/ProcessingMessage'] }}");

            //Get DateTime values for Start and End
            let startDate = createDatetTimeValue(startDateElement.val(), startTimeElement.val());
            let endDate = createDatetTimeValue(endDateElement.val(), endTimeElement.val());

            //DateTime value for deadline
            let deadlineDate = null;
            if (deadlineElement.val()) { deadlineDate = moment(deadlineElement.val().toString()); }

            //Put the values of the selected types in a string to send through the API
            let typeValues = Array.parse(formatElement.val())?.map(obj => {
                return obj.Value
            }).toString();

            //Put the values of the selected days of the week in a string
            let daysString = arrayToString(selectedDays);

            //Put the values of the grades in a string
            let gradeValues = Array.parse(gradeElement.val())?.map(obj => {
                return obj.Value
            }).toString();

            //Get if one-time event or recurring
            let chosenFreq = 192350001;
            if (frequencyElement.prop('checked')) { chosenFreq = 192350000; }

            //Round the capacity number
            let capacity = Math.round(capacityElement.val());

            //Create the Course data object
            var dataObject = {
                "msdynce_coursename": nameElement.val(),
                "msdynce_frequency": chosenFreq,
                "msdynce_coursedays": daysString,
                "msdynce_coursetype": typeValues,
                "msdynce_coursestartdateandtime": startDate,
                "msdynce_courseenddateandtime": endDate,
                "msdynce_coursecategory": categoryElement.val(),
                "msdynce_registerationmaxcapacity": capacity,
                "msdynce_coursegradelevel": gradeValues,
                "msdynce_registerationdeadline": deadlineDate,
                "msdynce_coursedescription": descriptionElement.val()
            }

            //If an instructor is selected, add it to the object (Lookups can't be set to null)
            if (instructorElement.val())
                dataObject['msdynce_InstructorName@odata.bind'] = "/contacts(" + instructorElement.val() + ")";

            if (documentbodyContents) {
                dataObject['msdynce_entityimage'] = documentbodyContents;
                dataObject['msdynce_entityimage_file_name'] = courseImageName;
                dataObject['msdynce_entityimage_file_type'] = courseImageType.substring(courseImageType.indexOf('/') + 1);
            } else if (imgBaseString != "") {
                dataObject['msdynce_entityimage'] = imgBaseString;
                dataObject['msdynce_entityimage_file_name'] = "{{courseselected.results.entities[0].msdynce_entityimage_file_name}}";
                dataObject['msdynce_entityimage_file_type'] = "{{courseselected.results.entities[0].msdynce_entityimage_file_type}}";
            }

            //Call the API
            webapi.safeAjax({
                type: "POST",
                url: "/_api/msdynce_courses",
                contentType: "application/json",
                data: JSON.stringify(dataObject),
                success: function (res, status, xhr) {
                    //print id of newly created table record
                    const createdCourseId = xhr.getResponseHeader("entityid");
                    //Redirect back to home
                    window.location.href = "{{ sitemarkers['C1 Home'].url }}?created=true&id=" + createdCourseId;
                },
                error: function (res, status, xhr) {
                    console.log(res.responseText);
                }
            });
        } else {
            console.log(errorMessages);
        }
    });

    let discardBtn = document.getElementById("confirmDiscard");
    discardBtn.onclick = function (event) {
        window.location.href = "{{ sitemarkers['C1 Home'].url }}";
    }
});

